INSERT INTO `hotel` VALUES ('1', '逍遥品悟耕读缘', '朗月借30号', '     躬耕养性，闲读怡情。寻觅村语，体验农事，自在耕读，品悟有时，亦耕亦读，不藉秋风亦成蝉。耕道猎德于民是本，读山品水以食为天，有缘者无论何方自然能遇见，不用寻寻觅觅，我一直在新叶古村——中国最美农耕村落等你，联系电话：13606519120，联系人：陈小娟', '10', '120', '15', '150', '1-1.jpg', '1-2.jpg', '1-3.jpg', '1-4.jpg', '2');
INSERT INTO `hotel` VALUES ('2', '生态绿色永旋农庄', '位于新叶村南，距西山祠堂100米', '永旋农庄（原名华峰住宿）位于新叶村南，距西山祠堂100米，家园菜园相连，当场点菜，可现场采摘，非常方便，生态绿色看得见摸得着，女主人夏海花烧得一手好菜。游新叶古村，品农家风味，永旋农庄期待你的光临，联系电话：15267493157  夏海花', '33', '134', '12', '123', '2-1.jpg', '2-2.jpg', '2-3.jpg', '2-4.jpg', '0');
INSERT INTO `hotel` VALUES ('3', '休闲娱乐古居农家', '新叶村入口500米处', '新叶古居农家是新叶最早提供餐饮服务的农家之一，面积虽然不大，但一年到头非常热闹，回头客很多。 想见证的话，请记住：联系电话0571-64535269 手机叶文康：13868107056', '44', '177', '44', '144', '3-1.jpg', '3-2.jpg', '3-3.jpg', '3-4.jpg', '0');
INSERT INTO `hotel` VALUES ('4', '古色古香明香园', '新叶牌坊附近', '新叶明香园，说是休闲山庄，实际上是一幢小小的二层楼，楼不大，但很有特色，特别是古件多，有些还是平量不易见到的东东……厨艺如何？最好有机会来尝尝，想品尝的游客准备好了，不要错过啊，老板：叶友标，联系电话：13064776010 13858172974', '22', '12', '122', '147', '4-1.jpg', '4-2.jpg', '4-3.jpg', '4-4.jpg', '0');
INSERT INTO `hotel` VALUES ('5', '香格里拉酒店', '北京西路1号', '豪华五星级大酒店', '200', '200', '600', '800', 'u=1656766312,1816703505&fm=11&gp=0.jpg', '1421121719.jpg', '23215223.jpg', '10287281_719762.jpg', '0');
INSERT INTO `hotel` VALUES ('6', '喜来登大酒店', '新叶景区', '国际知名、连锁五星级大酒店', '500', '500', '500', '600', '062c6c531c410865a67d971b08de6b1c_640_640.jpg', '12976668434d58d31b3837a.jpg', 's.jpg', '2.jpg', '0');

