DROP TABLE IF EXISTS `product`;
CREATE TABLE `product` (
  `prid` int(11) NOT NULL,
  `prname` varchar(255) NOT NULL,
  `prprice` int(11) DEFAULT NULL,
  `prjs` text,
  `prpic` varchar(255) DEFAULT NULL,
  PRIMARY KEY (`prid`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

