-- ----------------------------
-- Table structure for `scenic`
-- ----------------------------
DROP TABLE IF EXISTS `scenic`;
CREATE TABLE `scenic` (
  `scid` int(11) NOT NULL,
  `scname` varchar(255) NOT NULL,
  `sclocal` varchar(255) DEFAULT NULL,
  `scprice` int(11) DEFAULT NULL,
  `scjs` text,
  `sctj` text,
  `scpic1` varchar(255) DEFAULT NULL,
  `scpic2` varchar(255) DEFAULT NULL,
  `scpic3` varchar(255) DEFAULT NULL,
  `scpic4` varchar(255) DEFAULT NULL,
  PRIMARY KEY (`scid`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of scenic
-- ----------------------------
INSERT INTO `scenic` VALUES ('1', '玉泉寺 ', '玉泉寺坐落在玉华山与道峰山之间峡谷的最高处，因为位于新叶村外溪水源而得名。', '0', '玉泉寺大殿三开间，进深较大，柱网整齐，梁柱粗壮，中间大金柱直径为50厘米，柱础直径达80厘米，装饰精细，使用彩绘。大殿明间正门两侧作八字形影壁墙，墙上分别墨绘尉迟恭与秦叔宝。神采奕奕。檐柱间做黑色签子门，高抵横枋。次间用砖墙封闭，无窗。墙作红色。\"骑门梁\"和檐下构件雕饰十分丰富繁丽，配合红墙，以暗红色为主调，勾以蓝线条，凝重庄严。', '山高残雪冻云根，笋轿咿哑村复村，莫道山中无乐事，梅花涧水日黄昏。', 'yuquansi1.jpg', 'yuquansi2.jpg', 'yuquansi3.jpg', 'yuquansi4.jpg');
INSERT INTO `scenic` VALUES ('2', '万萃堂', '村东抟云塔北侧', '0', '万萃堂，西山祠堂的中亭，常被村民们作为西山祠堂的代称，为新叶村玉华叶氏祖庙，初建于元代。由叶氏三世祖叶克诚（一二五零至一三二三，字敬之，号东谷，行季六）主持兴建，以后多次重建、扩建，终于成了现状。到了明代，叶良觐（一四七一至一五五零，字时异，号文山，行暹三十三）、叶天祥（一四八七至一五六一，字君瑞，号友松，行暹六十二）兄弟主持宗族事务，于嘉靖乙未年（一五三五）发起重修，建筑呈“回字型”。清康熙八年（一六六九）由精堪舆的族人叶溥原（一六二零至一七零六，字普侯，号易奄，行泰三百零四）等将祖庙迁至村东抟云塔北侧，规模比原西山祠堂扩大了两三倍，更加气派。', ' 万萃堂，西山祠堂的中亭，常被村民们作为西山祠堂的代称，为新叶村玉华叶氏祖庙，初建于元代。', 'wancui1.jpg', 'wancui2.jpg', 'wancui3.jpg', 'wancui4.jpg');
INSERT INTO `scenic` VALUES ('3', '是亦居', '新叶南塘边', '0', '走进是亦居，最吸引人眼球的是石水缸， 石水缸是用大石头凿成的，浑然天成，放在天井里。以前养过金鱼，小孩子常常跑过来看，有时还用小棍子逗着玩。新叶的石水缸现在还有很多，有方的、椭圆形的、半圆的、圆的，但都没这只精致与完美，几乎与炽水缸的形状一致。水缸的功能众多，即可以养鱼观赏、蓄水防火，又意积水蓄财，在我的印象中，这水缸里的水从未断过，如果新叶旅游开放后，我倒希望这只水缸能重新养上小金鱼，观赏过后，倘若有游客投几枚硬币，也算是时时有余，蓄水蓄财了。', '清华大学建筑学院教授、乡土建筑保护专家陈志华对它的评价是“明清建筑的杰出代表”', 'shiyiju1.jpg', 'shiyiju2.jpg', 'shiyiju3.jpg', 'shiyiju4.jpg');
INSERT INTO `scenic` VALUES ('4', '双美堂', '县西四十里——儒源，叶克诚延仁山先生讲学处', '0', '双美堂”门楣上醒目地写着“耕读传家”四个字，新叶村这座具有八百年历史的村落除了保存了古老的建筑之外，还保持着原始而独特的耕读文化。\r\n\r\n“耕读”是中国古代知识分子所崇尚的半耕半读生活方式。这里的村民日出而作，日落而息，享受着最原始也最简朴的耕作方式，同时他们十分重视子弟读书，村中最显眼的“抟云塔”就是祈求文运的风水塔，不远处的重乐书院是叶氏家族培养学子的地方，除此之外村内还有私塾、学堂、书房等。在新叶村中游玩，不妨也像星爸萌娃们一样，穿上汉服在重乐书院读经典，或者试试赶牛、磨豆腐等农活，在都市的快节奏中忙里偷闲，感受下古代人的“耕读”慢生活！', '建于民国初年，座南面北，宅主属崇仁堂派，是当时村中七大乡绅之一。', 'shuangmeitang1.jpg', 'shuangmeitang2.jpg', 'shuangmeitang3.jpg', 'shuangmeitang4.jpg');
INSERT INTO `scenic` VALUES ('5', '大慈岩', '新叶古村南部', '0', '大慈岩是一个佛教文化和秀山丽水结合完美的旅游胜地、大慈岩素有“浙西小九华”之誉。大慈岩以江南悬空寺、长谷溪流、全国第一天然立佛而闻名遐迩。据县志载，元大德年间，临安人莫子渊循梦意弃家来此、琢石为佛，号曰大慈。山以佛名，大慈岩一名由此而来', '素有“浙西小九华”之誉，以“江南悬空寺、长谷溪流、全国第一天然立佛”而闻名遐迩，是“浙江省风景名胜区优秀景点”之一', 'dacisi1.jpg', 'dacisi2.jpg', 'dacisi3.jpg', 'dacisi4.jpg');
INSERT INTO `scenic` VALUES ('6', '大山', '新叶古镇', '20', '很漂亮的山顶', '很漂亮的山顶', '22.jpg', 'bmw.jpg', 'm6.jpg', 'bm01.png');

